package com.example.telemed.ui.doctor\n\nimport androidx.compose.foundation.layout.*\nimport androidx.compose.foundation.lazy.LazyColumn\nimport androidx.compose.foundation.lazy.items\nimport androidx.compose.material3.*\nimport androidx.compose.runtime.Composable\nimport androidx.compose.ui.Modifier\nimport androidx.compose.ui.graphics.Color\nimport androidx.compose.ui.text.font.FontWeight\nimport androidx.compose.ui.tooling.preview.Preview\nimport androidx.compose.ui.unit.dp\nimport com.example.telemed.data.Appointment\nimport com.example.telemed.data.ConsultationMethod\nimport com.example.telemed.ui.theme.TelemedTheme\nimport java.text.SimpleDateFormat\nimport java.util.Date\nimport java.util.Locale\n\n@Composable\nfun DoctorDashboardScreen(\n    appointments: List<Appointment>,\n    onAppointmentClicked: (Appointment) -> Unit\n) {\n    Scaffold(\n        topBar = {\n            TopAppBar(\n                title = { Text(\"Doctor Dashboard\") },\n                colors = TopAppBarDefaults.topAppBarColors(\n                    containerColor = MaterialTheme.colorScheme.primary,\n                    titleContentColor = Color.White\n                )\n            )\n        }\n    ) {\ paddingValues ->\n        LazyColumn(\n            modifier = Modifier\n                .fillMaxSize()\n                .padding(paddingValues)\n                .padding(16.dp),\n            verticalArrangement = Arrangement.spacedBy(16.dp)\n        ) {\n            item {\n                Text(\n                    text = \"Upcoming Appointments\",\n                    style = MaterialTheme.typography.headlineMedium,\n                    fontWeight = FontWeight.Bold\n                )\n            }\n            if (appointments.isEmpty()) {\n                item {\n                    Text(\"No upcoming appointments.\")\n                }\n            } else {\n                items(appointments) { appointment ->\n                    AppointmentCard(appointment, onAppointmentClicked)\n                }\n            }\n        }\n    }\n}\n\n@OptIn(ExperimentalMaterial3Api::class)\n@Composable\nfun AppointmentCard(appointment: Appointment, onAppointmentClicked: (Appointment) -> Unit) {\n    val timeFormatter = SimpleDateFormat(\"hh:mm a\", Locale.getDefault())\n    val date = Date(appointment.appointmentTime)\n\n    Card(\n        modifier = Modifier.fillMaxWidth(),\n        elevation = CardDefaults.cardElevation(4.dp),\n        onClick = { onAppointmentClicked(appointment) }\n    ) {\n        Column(modifier = Modifier.padding(16.dp)) {\n            Text(\"Patient: ${appointment.patientId}\", fontWeight = FontWeight.Bold) // Placeholder\n            Text(\"Time: ${timeFormatter.format(date)}\")\n            Text(\"Method: ${appointment.consultationMethod.name}\")\n        }\n    }\n}\n\n@Preview(showBackground = true)\n@Composable\nfun DoctorDashboardScreenPreview() {\n    TelemedTheme {\n        val sampleAppointments = listOf(\n            Appointment(\"1\", \"patient1\", \"doc1\", System.currentTimeMillis(), ConsultationMethod.VIDEO, \"Upcoming\"),\n            Appointment(\"2\", \"patient2\", \"doc1\", System.currentTimeMillis() + 3600000, ConsultationMethod.AUDIO, \"Upcoming\")\n        )\n        DoctorDashboardScreen(appointments = sampleAppointments, onAppointmentClicked = {})\n    }\n}\n